#include<iostream>#include<algorithm>using namespace std;int main(){	long long testcase, arr[3001],siz;	cin >> testcase;	while (testcase--)	{		cin >> siz;		long long max_seq=0;		for (int i = 0; i < siz; i++)			cin >> arr[i];		sort(arr, arr + siz);		for (int i = 0; i < siz; i++)		{			long long count = 1;			for (int j = i; j < siz-1; j++)			{				if (arr[j] == (arr[j + 1] - 1))					count++;				else if (arr[j] == arr[j + 1])					continue;				else				{					break;				}			}			if (max_seq < count)				max_seq = count;		}		cout << max_seq<<"\n";	}	return 0;}